sources.styles/blocks/button.scss:
.button {
  &--main {
    box-sizing: border-box;
    height: 56px;
    width: 100%;
    background-color: $bg-color;
    border: 2px solid $accent-orange-color;
    border-radius: 32px;
    padding: 16px;
    cursor: pointer;

    display: flex;
    justify-content: center;
    align-items: center;
    gap: 4px;

    transition: all $transition-duration;

    grid-column: 1 / -1;

    @include onTablet {
      grid-column: 3 / -3;
    }

    @include onDesktop {
      grid-column: 1 / 3;
    }

    &-link {
      @extend %text-upper;
      text-decoration: none;
      color: $accent-orange-color;
    }

    &::before {
      content: "";
      @extend %icon-style;
      margin-right: 4px;

      background-image: url(../images/icons/icon-cookie.svg);
    }

    &:hover {
      background-color: $accent-orange-color;

      .button--main-link {
        color: $white;
      }

      &::before {
        background-image: url(../images/icons/icon-cookie-white.svg);
      }
    }

    &:active {
      background-color: $dark-orange-color;
      border: 2px solid $dark-orange-color;
    }

    &:disabled {
      background-color: $bg-color;
      border: 2px solid $light-orange-color;
      color: $light-orange-color;

      &::before {
        background-image: url(../images/icons/icon-cookie-light.svg);
      }
    }
  }

  &--order {
    box-sizing: border-box;
    height: 32px;
    width: max-content;
    display: flex;
    justify-content: center;
    align-items: center;
    gap: 4px;
    cursor: pointer;

    background-color: $bg-color;
    border: none;
    border-bottom: 1px solid transparent;

    transition: border-color $transition-duration;

    &-link {
      @extend %text-upper;
      text-decoration: none;
    }

    &:hover {
      border-bottom: 1px solid $accent-orange-color;
    }
  }
}

